function isDivisibleBy(num, divisor) {
  return num % divisor === 0;
}

function hypotenuse(a, b) {
  return Math.sqrt(a * a + b * b);
}

function clamp(num, min, max) {
  if (num < min) return min;
  if (num > max) return max;
  return num;
}

function discountPrice(price, discountPercent) {
  return price * (1 - discountPercent / 100);
}

function isOdd(num) {
  return num % 2 !== 0;
}

function bothTruthy(a, b) {
  return !!a && !!b;
}

function eitherTruthy(a, b) {
  return !!a || !!b;
}

function bmiCategory(bmi) {
  if (bmi < 18.5) return "Underweight";
  if (bmi < 25)   return "Normal";
  if (bmi < 30)   return "Overweight";
  return "Obese";
}

function nextLight(color) {
  if (color === "red")   return "green";
  if (color === "green") return "yellow";
  return "red";
}

function getDayName(num) {
  const days = ["Monday","Tuesday","Wednesday","Thursday","Friday","Saturday","Sunday"];
  if (num < 1 || num > 7) return "Invalid";
  return days[num - 1];
}

function sumOfSquares(n) {
  let sum = 0;
  for (let i = 1; i <= n; i++) {
    sum += i * i;
  }
  return sum;
}

function countOccurrences(arr, target) {
  let count = 0;
  for (let i = 0; i < arr.length; i++) {
    if (arr[i] === target) count++;
  }
  return count;
}

function largestInArray(arr) {
  let largest = arr[0];
  for (let i = 1; i < arr.length; i++) {
    if (arr[i] > largest) largest = arr[i];
  }
  return largest;
}

exercises.instructions:
The following for loop doubles each number in an array:
```js
const result = [];
for (let i = 0; i < arr.length; i++) {
  result.push(arr[i] * 2);
}
```

Write a function called `doubleWithForEach` that produces the same result using `.forEach()` instead.

Examples:
  doubleWithForEach([1, 2, 3]) → [2, 4, 6]
  doubleWithForEach([5, 10])   → [10, 20]

exercises.starterCode:
function doubleWithForEach(arr) {
  const result = [];
  // Replace the for loop with .forEach()

  return result;
}

exercises.solution:
function doubleWithForEach(arr) {
  const result = [];
  arr.forEach(num => result.push(num * 2));
  return result;
}

