class Temperature {
  constructor(degrees, scale = "C") {
    this.degrees = degrees;
    this.scale = scale;
  }

  toCelsius() {
    if (this.scale === "C") return this.degrees;
    return (this.degrees - 32) * 5 / 9;
  }

  toFahrenheit() {
    if (this.scale === "F") return this.degrees;
    return (this.degrees * 9 / 5) + 32;
  }

  isFreezing() {
    return this.toCelsius() <= 0;
  }
}

