exercises.solution:
class Playlist {
  constructor(name) {
    this.name = name;
    this.songs = [];
    this.currentIndex = -1;
  }

  addSong(title, artist) {
    this.songs.push({ title, artist });
  }

  removeSong(title) {
    const index = this.songs.findIndex(s => s.title === title);
    if (index !== -1) this.songs.splice(index, 1);
  }

  play() {
    if (this.songs.length === 0) return null;
    this.currentIndex = 0;
    return this.songs[0];
  }

  skip() {
    if (this.songs.length === 0) return null;
    this.currentIndex = (this.currentIndex + 1) % this.songs.length;
    return this.songs[this.currentIndex];
  }

  nowPlaying() {
    if (this.currentIndex === -1 || this.songs.length === 0) return null;
    return this.songs[this.currentIndex];
  }

  songCount() {
    return this.songs.length;
  }
}

