exercises.solution:
class Library {
  constructor(name) {
    this.name = name;
    this.catalog = {};
  }

  addBook(title, copies = 1) {
    this.catalog[title] = { copies, checkedOut: 0 };
  }

  checkout(title) {
    if (!this.isAvailable(title)) return "Sorry, " + title + " is not available";
    this.catalog[title].checkedOut++;
    return "Enjoy " + title + "!";
  }

  returnBook(title) {
    if (this.catalog[title] && this.catalog[title].checkedOut > 0) {
      this.catalog[title].checkedOut--;
    }
    return "Thank you for returning " + title + "!";
  }

  isAvailable(title) {
    return this.availableCopies(title) > 0;
  }

  availableCopies(title) {
    if (!this.catalog[title]) return 0;
    return this.catalog[title].copies - this.catalog[title].checkedOut;
  }
}

