class Garage {
  constructor(capacity) {
    this.capacity = capacity;
    this.cars = [];
  }

  addCar(licensePlate) {
    if (this.isFull()) return "Garage is full";
    this.cars.push(licensePlate);
    return licensePlate + " parked successfully";
  }

  removeCar(licensePlate) {
    const index = this.cars.indexOf(licensePlate);
    if (index === -1) return licensePlate + " not found";
    this.cars.splice(index, 1);
    return licensePlate + " has left the garage";
  }

  isFull() {
    return this.cars.length >= this.capacity;
  }

  availableSpots() {
    return this.capacity - this.cars.length;
  }

  hasPlate(licensePlate) {
    return this.cars.includes(licensePlate);
  }
}

