exercises.solution:
function createPluginSystem() {
  const plugins = [];
  return {
    register: function(name, fn) {
      plugins.push({ name: name, fn: fn });
    },
    execute: function(input) {
      let result = input;
      for (const plugin of plugins) {
        result = plugin.fn(result);
      }
      return result;
    },
    list: function() {
      return plugins.map(function(p) { return p.name; });
    },
    remove: function(name) {
      const idx = plugins.findIndex(function(p) { return p.name === name; });
      if (idx !== -1) plugins.splice(idx, 1);
    }
  };
}

