class RequestCache {
  constructor(defaultTTL) {
    // your code here
  }

  set(url, data, ttl) {}
  get(url) {}
  has(url) {}
  clear() {}
  get size() {}
}

exercises.solution:
class RequestCache {
  constructor(defaultTTL) {
    this._defaultTTL = defaultTTL;
    this._cache = {};
  }

  set(url, data, ttl) {
    this._cache[url] = {
      data: data,
      expiry: Date.now() + (ttl !== undefined ? ttl : this._defaultTTL)
    };
  }

  get(url) {
    const entry = this._cache[url];
    if (!entry) return null;
    if (Date.now() > entry.expiry) {
      delete this._cache[url];
      return null;
    }
    return entry.data;
  }

  has(url) {
    return this.get(url) !== null;
  }

  clear() {
    this._cache = {};
  }

  get size() {
    const now = Date.now();
    let count = 0;
    for (const url in this._cache) {
      if (this._cache[url].expiry > now) count++;
    }
    return count;
  }
}

