exercises.solution:
:root {
  --primary-color: #3b82f6;
}

.box {
  background-color: var(--primary-color);
}

:root {
  --bg-color: #0f172a;
  --text-color: #e2e8f0;
  --accent-color: #f59e0b;
}

.page {
  background-color: var(--bg-color);
  color: var(--text-color);
}

.highlight {
  color: var(--accent-color);
}

.card {
  background-color: var(--card-bg, #1e293b);
  color: var(--card-text, #f1f5f9);
  padding: 20px;
}

.container {
  width: calc(100% - 40px);
  padding: 20px;
  height: 200px;
}

:root {
  --spacing: 16px;
}

.box {
  padding: var(--spacing);
  margin: calc(var(--spacing) * 2);
  width: 200px;
}

.container {
  width: min(100%, 600px);
  min-height: max(200px, 50vh);
  padding: 16px;
}

.heading {
  font-size: clamp(1.5rem, 4vw, 3rem);
  line-height: 1.2;
}

.body-text {
  font-size: clamp(1rem, 2vw, 1.25rem);
}

.wrapper {
  width: clamp(320px, 80%, 960px);
  margin: 0 auto;
  padding: 24px;
}

:root {
  --sidebar-width: 250px;
}

.main-content {
  width: calc(100% - var(--sidebar-width));
  padding: clamp(16px, 3vw, 48px);
  min-height: 200px;
}

:root {
  --space-xs: 4px;
  --space-sm: 8px;
  --space-md: 16px;
  --space-lg: 32px;
}

.card {
  padding: var(--space-md);
  margin-bottom: var(--space-lg);
}

.card-title {
  margin-bottom: var(--space-sm);
}

:is(h1, h2, h3) {
  color: #3b82f6;
  margin-bottom: 12px;
}

:where(h1, h2, h3) {
  font-weight: 600;
  line-height: 1.3;
}

h1 {
  color: #ef4444;
}

.item:not(:last-child) {
  margin-bottom: 12px;
}

.item:not(.disabled) {
  color: #e2e8f0;
}

.item.disabled {
  opacity: 0.4;
}

.card {
  border-radius: 8px;
}

.card:has(img) {
  padding: 0;
}

.card:not(:has(img)) {
  padding: 24px;
}

.form-group {
  padding-left: 12px;
}

.form-group:has(.error) {
  border-left: 3px solid #ef4444;
}

.form-group:not(:has(.error)) {
  border-left: 3px solid #22c55e;
}

[data-status="active"] {
  color: #22c55e;
}

[data-status="inactive"] {
  color: #94a3b8;
}

[data-status="error"] {
  color: #ef4444;
}

:is(h1, h2) {
  font-family: Georgia, serif;
}

.list-item:not(:last-child) {
  border-bottom: 1px solid #334155;
}

.section:has(.highlight) {
  background-color: rgba(59, 130, 246, 0.1);
}

.section:not(:has(.highlight)) {
  background-color: transparent;
}

.video-container {
  aspect-ratio: 16 / 9;
  width: 100%;
  max-width: 640px;
  background-color: #1e293b;
}

.thumbnail {
  object-fit: cover;
  width: 200px;
  height: 200px;
  border-radius: 8px;
}

.card {
  padding-inline: 24px;
  padding-block: 16px;
  margin-block-end: 20px;
  border-inline-start: 4px solid #3b82f6;
}

.toolbar {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 16px;
}

.grid-parent {
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 16px;
}

.grid-child {
  display: grid;
  grid-template-columns: subgrid;
  grid-column: 1 / -1;
}

.grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 16px;
}

.min-box {
  width: min-content;
  padding: 12px;
  margin-bottom: 8px;
}

.max-box {
  width: max-content;
  padding: 12px;
  margin-bottom: 8px;
}

.fit-box {
  width: fit-content(300px);
  padding: 12px;
  margin-bottom: 8px;
}

.glass-panel {
  backdrop-filter: blur(10px);
  background-color: rgba(15, 23, 42, 0.6);
  padding: 24px;
  border-radius: 12px;
}

.mixed-bg {
  background-color: color-mix(in srgb, #3b82f6 50%, #ef4444 50%);
  padding: 24px;
  color: white;
}

.form-controls {
  accent-color: #8b5cf6;
  font-size: 16px;
}

.form-controls input {
  margin-right: 8px;
}

.carousel {
  scroll-snap-type: x mandatory;
  display: flex;
  overflow-x: auto;
  gap: 16px;
}

.slide {
  scroll-snap-align: start;
  min-width: 300px;
  flex-shrink: 0;
}

html {
  scroll-behavior: smooth;
}

.section {
  min-height: 200px;
  padding: 20px;
}

.btn {
  padding: 12px 24px;
  border-radius: 6px;
  border: 1px solid #475569;
}

.btn:focus-visible {
  outline: 2px solid #3b82f6;
  outline-offset: 3px;
}

.card-wrapper {
  container-type: inline-size;
}

.card {
  padding: 16px;
}

@container (min-width: 400px) {
  .card {
    padding: 32px;
  }
}

.widget-container {
  container-type: inline-size;
}

.widget {
  font-size: 14px;
  display: block;
}

@container (min-width: 300px) {
  .widget {
    font-size: 16px;
  }
}

@container (min-width: 500px) {
  .widget {
    font-size: 18px;
    display: flex;
    gap: 16px;
  }
}

.sidebar {
  container-type: inline-size;
  container-name: sidebar;
}

.main {
  container-type: inline-size;
  container-name: main;
}

@container sidebar (min-width: 200px) {
  .nav-item {
    font-size: 14px;
  }
}

@container main (min-width: 400px) {
  .content {
    font-size: 18px;
  }
}

.card {
  padding: 20px;
  border-radius: 8px;

  .card-title {
    font-size: 20px;
    margin-bottom: 8px;
  }

  .card-body {
    font-size: 14px;
  }
}

.hero {
  padding: 20px;
  font-size: 16px;

  @media (min-width: 768px) {
    padding: 40px;
    font-size: 24px;
  }
}

.btn {
  padding: 12px 24px;
  background-color: #3b82f6;
  color: white;
  border-radius: 6px;
  border: none;

  &:hover {
    background-color: #2563eb;
  }

  &:active {
    background-color: #1d4ed8;
  }
}

:root {
  --color-primary: #3b82f6;
  --color-surface: #1e293b;
  --color-text: #e2e8f0;
  --font-body: 'Inter', sans-serif;
  --radius-md: 8px;
  --shadow-md: 0 4px 12px rgba(0, 0, 0, 0.3);
}

.card {
  background-color: var(--color-surface);
  color: var(--color-text);
  font-family: var(--font-body);
  border-radius: var(--radius-md);
  box-shadow: var(--shadow-md);
  padding: 24px;
}

.card-container {
  container-type: inline-size;
}

.card {
  padding: clamp(16px, 4cqw, 32px);
  width: min(100%, 500px);
  font-size: clamp(14px, 2cqw, 18px);
  border-radius: 8px;
}

[data-theme="dark"] {
  --bg: #0f172a;
  --text: #e2e8f0;
  --surface: #1e293b;
}

[data-theme="light"] {
  --bg: #ffffff;
  --text: #1e293b;
  --surface: #f1f5f9;
}

.page {
  background-color: var(--bg);
  color: var(--text);
}

.card {
  background-color: var(--surface);
  padding: 20px;
}

:root {
  --gap: 16px;
  --radius: 8px;
}

.dashboard {
  display: grid;
  grid-template-columns: 250px 1fr;
  gap: var(--gap);
  container-type: inline-size;
}

.panel {
  background-color: #1e293b;
  border-radius: var(--radius);
  padding: clamp(12px, 2cqw, 24px);

  .panel-title {
    font-size: 18px;
    margin-bottom: var(--gap);
  }
}

@container (max-width: 600px) {
  .dashboard {
    grid-template-columns: 1fr;
  }
}
