function twoHighest(arr) {
  let highest = -Infinity;
  let second = -Infinity;
  for (let i = 0; i < arr.length; i++) {
    if (arr[i] >= highest) {
      second = highest;
      highest = arr[i];
    } else if (arr[i] > second) {
