  // Return [ [sumOfNumbers], [totalStringLength] ]

}

exercises.solution:
function divideObject(obj) {
  let numSum = 0;
  let strLen = 0;
  for (let key in obj) {
    if (typeof obj[key] === 'number') numSum += obj[key];
    else strLen += obj[key].length;
  }
  return [[numSum], [strLen]];
}

