  // Your code here

}

exercises.solution:
function findFirstAndLastIndex(arr, num) {
  let first = -1;
  let last = -1;
  for (let i = 0; i < arr.length; i++) {
    if (arr[i] === num) {
      if (first === -1) first = i;
      last = i;
    }
  }
  if (first === last) return -1;
  return [first, last];
}

