    { task: "Eat", priority: 18 },
    { task: "Sleep", priority: 22 },
    { task: "Solve problems", priority: 17 }
  ]) // ["Sleep", 22]

  findHighestPriorityTodo([
    { task: "Task 1", priority: 1 },
    { task: "Task 2", priority: 2 },
    { task: "Task 3", priority: 3 }
  ]) // ["Task 3", 3]

exercises.starterCode:
