}

exercises.solution:
function findHighestPriorityTodo(todos) {
  let best = todos[0];
  for (let i = 1; i < todos.length; i++) {
    if (todos[i].priority > best.priority) best = todos[i];
  }
  return [best.task, best.priority];
}

