exercises.instructions:
Write a function called `myForEach` that accepts an array and a callback, and calls the callback once per element passing (element, index). It should not return anything.

Examples:
  const result = [];
  myForEach([1, 2, 3], (el, i) => result.push(el * i));
  result → [0, 2, 6]  (1×0, 2×1, 3×2)

exercises.solution:
function myForEach(arr, callback) {
  for (let i = 0; i < arr.length; i++) {
    callback(arr[i], i);
  }
}

function myMap(arr, callback) {
  const result = [];
  for (let i = 0; i < arr.length; i++) {
    result.push(callback(arr[i], i));
  }
  return result;
}

function myFilter(arr, callback) {
  const result = [];
  for (let i = 0; i < arr.length; i++) {
    if (callback(arr[i], i)) result.push(arr[i]);
  }
  return result;
}

function myFind(arr, callback) {
  for (let i = 0; i < arr.length; i++) {
    if (callback(arr[i], i)) return arr[i];
  }
  return undefined;
}

function myEvery(arr, callback) {
  for (let i = 0; i < arr.length; i++) {
    if (!callback(arr[i], i)) return false;
  }
  return true;
}

function mySome(arr, callback) {
  for (let i = 0; i < arr.length; i++) {
    if (callback(arr[i], i)) return true;
  }
  return false;
}

function myReduce(arr, callback, initialValue) {
  let acc = initialValue;
  for (let i = 0; i < arr.length; i++) {
    acc = callback(acc, arr[i], i);
  }
  return acc;
}

function countVowels(str) {
  const vowels = "aeiouAEIOU";
  let count = 0;
  for (let i = 0; i < str.length; i++) {
    if (vowels.includes(str[i])) count++;
  }
  return count;
}

function truncate(str, maxLength) {
  if (str.length > maxLength) return str.slice(0, maxLength) + "...";
  return str;
}

function toSnakeCase(str) {
  let result = "";
  for (let i = 0; i < str.length; i++) {
    const char = str[i];
    if (char === char.toUpperCase() && char !== char.toLowerCase()) {
      result += "_" + char.toLowerCase();
    } else {
      result += char;
    }
  }
  return result;
}

function wordCount(str) {
  const words = str.split(" ");
  const counts = {};
  for (let i = 0; i < words.length; i++) {
    const word = words[i].toLowerCase();
    counts[word] = (counts[word] || 0) + 1;
  }
  return counts;
}

function toCamelCase(str) {
  const parts = str.split("_");
  return parts[0] + parts.slice(1).map(p => p[0].toUpperCase() + p.slice(1)).join("");
}

