class VendingMachine {
  constructor() {
    this.balance = 0;
    this.inventory = {};
  }

  addItem(name, price, quantity) {
    this.inventory[name] = { price, quantity };
  }

  insertMoney(amount) {
    this.balance += amount;
    return this.balance;
  }

  selectItem(name) {
    const item = this.inventory[name];
    if (!item || item.quantity === 0) return name + " is out of stock";
    if (this.balance < item.price) return "Insufficient funds";
    this.balance -= item.price;
    item.quantity--;
    const change = Math.round(this.balance * 100) / 100;
    return "Dispensing " + name + ". Change: $" + change.toFixed(2);
  }

  getChange() {
    const change = this.balance;
    this.balance = 0;
    return change;
  }

  getInventory() {
    return this.inventory;
  }
}

