exercises.solution:
class Timer {
  constructor() {
    this.seconds = 0;
    this.isRunning = false;
  }

  start() {
    if (this.isRunning) return "Timer already running";
    this.isRunning = true;
    return "Timer started";
  }

  stop() {
    this.isRunning = false;
    return "Timer stopped";
  }

  tick() {
    if (this.isRunning) this.seconds++;
    return this.seconds;
  }

  reset() {
    this.seconds = 0;
    this.isRunning = false;
  }

  getFormatted() {
    const mins = Math.floor(this.seconds / 60);
    const secs = this.seconds % 60;
    return String(mins).padStart(2, "0") + ":" + String(secs).padStart(2, "0");
  }
}

