exercises.solution:
class Character {
  constructor(name, role) {
    this.name = name;
    this.role = role;
    this.health = 100;
    this.level = 1;
    this.isAlive = true;
  }

  takeDamage(amount) {
    this.health = Math.max(0, this.health - amount);
    if (this.health === 0) this.isAlive = false;
  }

  heal(amount) {
    if (!this.isAlive) return;
    this.health = Math.min(100, this.health + amount);
  }

  levelUp() {
    this.level += 1;
    this.health = 100;
  }
}

class Party {
  constructor(name) {
    this.name = name;
    this.members = [];
  }

  addMember(character) {
    this.members.push(character);
  }

  isWiped() {
    return this.members.every(c => !c.isAlive);
  }

  getLivingMembers() {
    return this.members.filter(c => c.isAlive);
  }

  healAll(amount) {
    this.getLivingMembers().forEach(c => c.heal(amount));
  }

  averageLevel() {
    if (this.members.length === 0) return 0;
    const total = this.members.reduce((sum, c) => sum + c.level, 0);
    return total / this.members.length;
  }
}

