exercises.solution:
class Student {
  constructor(name, id) {
    this.name = name;
    this.id = id;
    this.grades = [];
  }

  addGrade(score) {
    this.grades.push(score);
  }

  getAverage() {
    if (this.grades.length === 0) return 0;
    return this.grades.reduce((sum, g) => sum + g, 0) / this.grades.length;
  }

  getLetterGrade() {
    const avg = this.getAverage();
    if (avg >= 90) return 'A';
    if (avg >= 80) return 'B';
    if (avg >= 70) return 'C';
    if (avg >= 60) return 'D';
    return 'F';
  }

  isHonorRoll() {
    return this.getAverage() >= 90;
  }
}

class Classroom {
  constructor(subject) {
    this.subject = subject;
    this.students = [];
  }

  enroll(student) {
    this.students.push(student);
  }

  getHonorRoll() {
    return this.students.filter(s => s.isHonorRoll());
  }

  classAverage() {
    if (this.students.length === 0) return 0;
    const total = this.students.reduce((sum, s) => sum + s.getAverage(), 0);
    return total / this.students.length;
  }

  getTopStudent() {
    return this.students.reduce((top, s) => s.getAverage() > top.getAverage() ? s : top, this.students[0]);
  }

  gradeDistribution() {
    const dist = { A: 0, B: 0, C: 0, D: 0, F: 0 };
    this.students.forEach(s => {
      dist[s.getLetterGrade()]++;
    });
    return dist;
  }
}

