class Appointment {
  constructor(clientName, service, duration, price) {
    this.clientName = clientName;
    this.service = service;
    this.duration = duration;
    this.price = price;
    this.status = 'booked';
  }

  complete() {
    this.status = 'completed';
  }

  cancel() {
    this.status = 'cancelled';
  }

  isActive() {
    return this.status === 'booked';
  }
}

class Spa {
  constructor(name) {
    this.name = name;
    this.appointments = [];
  }

  book(appointment) {
    this.appointments.push(appointment);
  }

  cancel(clientName) {
    const appt = this.appointments.find(a => a.clientName === clientName && a.isActive());
    if (!appt) return null;
    appt.cancel();
    return appt;
  }

  completeNext() {
    const appt = this.appointments.find(a => a.isActive());
    if (!appt) return null;
    appt.complete();
    return appt;
  }

  getRevenue() {
    return this.appointments
      .filter(a => a.status === 'completed')
      .reduce((sum, a) => sum + a.price, 0);
  }

  getSchedule() {
    return this.appointments.filter(a => a.isActive());
  }
}

