  const r2 = deepMerge({ a: 1 }, { b: 2 });
  const r3 = deepMerge({ a: { b: { c: 1 } } }, { a: { b: { d: 2 } } });
  const orig1 = { a: 1, b: { x: 10 } };
  const orig2 = { b: { y: 20 } };
  deepMerge(orig1, orig2);
  return [
