  return { building, totalFloors, currentFloor, passengers };
}

function changeFloors(elevator, newFloor) {
  if (newFloor === elevator.currentFloor) {
    return `You're already on floor ${newFloor}!`;
  }
  if (newFloor > elevator.totalFloors) {
    return `Floor ${newFloor} does not exist!`;
  }
  elevator.currentFloor = newFloor;
  return `Taking you to floor ${newFloor}!`;
}

function dropOffPassenger(elevator, name) {
  elevator.passengers = elevator.passengers.filter(p => p !== name);
}

