function arrowContext() {
  const obj = {
    name: 'obj',
    getNameArrow: () => this.name,
    getNameMethod() {
      const inner = () => this.name;
      return inner();
    },
  };
  return [typeof obj.getNameArrow(), obj.getNameMethod()];
}

function explicitBinding() {
  function greet() { return 'Hello, ' + this.name; }
  const person = { name: 'Alice' };
  const dog = { name: 'Rex' };
  const bound = greet.bind(person);
  return [greet.call(dog), bound()];
}

function classContext() {
  class Hero {
    constructor(name) { this.name = name; }
    announce() { return this.name + ' is here!'; }
  }
  const h = new Hero('Batman');
  return h.announce();
}

