exercises.solution:
function createValidator() {
  const rules = [];

  return {
    addRule(field, testFn, errorMsg) {
      rules.push({ field, testFn, errorMsg });
    },

    validate(obj) {
      const errors = [];
      for (const rule of rules) {
        if (!rule.testFn(obj[rule.field])) {
          errors.push(rule.errorMsg);
        }
      }
      return { valid: errors.length === 0, errors };
    },

    getRules() {
      return rules.length;
    },
  };
}

