exercises.solution:
function createStateMachine(initialState, transitions) {
  let current = initialState;
  const history = [];

  return {
    getState() {
      return current;
    },

    send(action) {
      const stateTransitions = transitions[current];
      if (!stateTransitions || !stateTransitions[action]) return false;
      const from = current;
      current = stateTransitions[action];
      history.push({ from, action, to: current });
      return current;
    },

    canSend(action) {
      const stateTransitions = transitions[current];
      return !!(stateTransitions && stateTransitions[action]);
    },

    getHistory() {
      return [...history];
    },
  };
}

