exercises.solution:
class Observable {
  constructor(initialValue) {
    this._value = initialValue;
    this._subscribers = new Set();
  }

  get() {
    return this._value;
  }

  set(value) {
    this._value = value;
    for (const cb of this._subscribers) cb(value);
  }

  subscribe(callback) {
    this._subscribers.add(callback);
    return () => this._subscribers.delete(callback);
  }

  map(transformFn) {
    const derived = new Observable(transformFn(this._value));
    this.subscribe(val => derived.set(transformFn(val)));
    return derived;
  }

  subscriberCount() {
    return this._subscribers.size;
  }
}

