exercises.solution:
class Node {
  constructor(value) {
    this.value = value;
    this.next = null;
  }
}

class LinkedList {
  constructor() {
    this.head = null;
    this.size = 0;
  }

  append(value) {
    const node = new Node(value);
    if (!this.head) { this.head = node; }
    else {
      let curr = this.head;
      while (curr.next) curr = curr.next;
      curr.next = node;
    }
    this.size++;
  }

  prepend(value) {
    const node = new Node(value);
    node.next = this.head;
    this.head = node;
    this.size++;
  }

  getAt(index) {
    if (index < 0 || index >= this.size) return null;
    let curr = this.head;
    for (let i = 0; i < index; i++) curr = curr.next;
    return curr.value;
  }

  removeAt(index) {
    if (index < 0 || index >= this.size) return null;
    let removed;
    if (index === 0) {
      removed = this.head.value;
      this.head = this.head.next;
    } else {
      let curr = this.head;
      for (let i = 0; i < index - 1; i++) curr = curr.next;
      removed = curr.next.value;
      curr.next = curr.next.next;
    }
    this.size--;
    return removed;
  }

  find(value) {
    let curr = this.head;
    let idx = 0;
    while (curr) {
      if (curr.value === value) return idx;
      curr = curr.next;
      idx++;
    }
    return -1;
  }

  reverse() {
    let prev = null;
    let curr = this.head;
    while (curr) {
      const next = curr.next;
      curr.next = prev;
      prev = curr;
      curr = next;
    }
    this.head = prev;
  }

  toArray() {
    const arr = [];
    let curr = this.head;
    while (curr) { arr.push(curr.value); curr = curr.next; }
    return arr;
  }

  toString() {
    return this.toArray().join(' -> ');
  }
}

