exercises.solution:
function albumsByGenre(albums) {
  return albums.reduce((acc, album) => {
    album.genres.forEach(genre => {
      if (!acc[genre]) acc[genre] = [];
      acc[genre].push(album.title);
    });
    return acc;
  }, {});
}

function artistStats(albums) {
  return albums.reduce((acc, album) => {
    if (!acc[album.artist]) acc[album.artist] = { albumCount: 0, totalTracks: 0, totalDuration: 0 };
    const stat = acc[album.artist];
    stat.albumCount++;
    stat.totalTracks += album.tracks.length;
    stat.totalDuration += album.tracks.reduce((sum, t) => sum + t.durationSec, 0);
    return acc;
  }, {});
  // Post-process to compute avg
}

function artistStats(albums) {
  const raw = albums.reduce((acc, album) => {
    if (!acc[album.artist]) acc[album.artist] = { albumCount: 0, totalTracks: 0, totalDuration: 0 };
    const stat = acc[album.artist];
    stat.albumCount++;
    stat.totalTracks += album.tracks.length;
    stat.totalDuration += album.tracks.reduce((sum, t) => sum + t.durationSec, 0);
    return acc;
  }, {});
  const result = {};
  for (const [artist, stat] of Object.entries(raw)) {
    result[artist] = {
      albumCount: stat.albumCount,
      totalTracks: stat.totalTracks,
      avgTrackDuration: Math.round(stat.totalDuration / stat.totalTracks),
    };
  }
  return result;
}

function decadeSummary(albums) {
  return albums.reduce((acc, album) => {
    const decade = Math.floor(album.year / 10) * 10 + 's';
    if (!acc[decade]) acc[decade] = { count: 0, titles: [] };
    acc[decade].count++;
    acc[decade].titles.push(album.title);
    return acc;
  }, {});
}

