exercises.solution:
class Range {
  constructor(start, end, step = 1) {
    this.start = start;
    this.end = end;
    this.step = step;
  }

  [Symbol.iterator]() {
    let current = this.start;
    const end = this.end;
    const step = this.step;
    return {
      next() {
        if ((step > 0 && current <= end) || (step < 0 && current >= end)) {
          const value = current;
          current += step;
          return { value, done: false };
        }
        return { done: true };
      },
    };
  }

  toArray() {
    return [...this];
  }

  includes(value) {
    if (this.step > 0) {
      return value >= this.start && value <= this.end && (value - this.start) % this.step === 0;
    }
    return value <= this.start && value >= this.end && (this.start - value) % Math.abs(this.step) === 0;
  }

  get length() {
    return Math.max(0, Math.floor((this.end - this.start) / this.step) + 1);
  }
}

