exercises.solution:
class Task {
  constructor(title, priority) {
    this.title = title;
    this.priority = priority;
    this.status = 'todo';
    this.assignee = null;
  }

  assign(user) {
    this.assignee = user.name;
  }
}

class Column {
  constructor(name, limit) {
    this.name = name;
    this.limit = limit;
    this.tasks = [];
  }

  addTask(task) {
    if (this.tasks.length >= this.limit) return false;
    this.tasks.push(task);
    return true;
  }

  removeTask(title) {
    const idx = this.tasks.findIndex(t => t.title === title);
    if (idx === -1) return null;
    return this.tasks.splice(idx, 1)[0];
  }

  get count() {
    return this.tasks.length;
  }

  getByPriority(priority) {
    return this.tasks.filter(t => t.priority === priority);
  }
}

class Board {
  constructor(name) {
    this.name = name;
    this.columns = [];
  }

  addColumn(column) {
    this.columns.push(column);
  }

  moveTask(title, fromCol, toCol) {
    const from = this.columns.find(c => c.name === fromCol);
    const to = this.columns.find(c => c.name === toCol);
    if (!from || !to) return false;
    const task = from.removeTask(title);
    if (!task) return false;
    if (!to.addTask(task)) {
      from.addTask(task);
      return false;
    }
    if (toCol === 'Done') task.status = 'done';
    return true;
  }

  get allTasks() {
    return this.columns.flatMap(c => c.tasks);
  }

  getTasksByUser(name) {
    return this.allTasks.filter(t => t.assignee === name);
  }

  get completionRate() {
    const all = this.allTasks;
    if (all.length === 0) return 0;
    const done = all.filter(t => t.status === 'done').length;
    return Math.round((done / all.length) * 100);
  }
}

class User {
  constructor(name) {
    this.name = name;
  }

  get taskCount() {
    return 0; // needs board reference — simplified
  }
}

