exercises.solution:
class Venue {
  constructor(name, capacity) {
    this.name = name;
    this.capacity = capacity;
    this.bookings = [];
  }

  isAvailable(date, startHour, endHour) {
    return !this.bookings.some(b => b.date === date && b.startHour < endHour && b.endHour > startHour);
  }

  book(event) {
    if (!this.isAvailable(event.date, event.startHour, event.endHour)) return false;
    this.bookings.push(event);
    return true;
  }

  cancelBooking(eventName) {
    this.bookings = this.bookings.filter(b => b.name !== eventName);
  }
}

class Attendee {
  constructor(name) {
    this.name = name;
    this._events = [];
  }

  rsvp(event) {
    const conflict = this._events.some(e => e.date === event.date && e.startHour < event.endHour && e.endHour > event.startHour);
    if (conflict) return false;
    this._events.push(event);
    return true;
  }

  cancel(eventName) {
    this._events = this._events.filter(e => e.name !== eventName);
  }

  get schedule() {
    return [...this._events].sort((a, b) => a.date < b.date ? -1 : a.date > b.date ? 1 : a.startHour - b.startHour);
  }
}

class Event {
  constructor(name, date, startHour, endHour, venue) {
    this.name = name;
    this.date = date;
    this.startHour = startHour;
    this.endHour = endHour;
    this.venue = venue;
    this.attendees = [];
  }

  addAttendee(attendee) {
    if (this.isFull) return false;
    this.attendees.push(attendee);
    return true;
  }

  removeAttendee(name) {
    this.attendees = this.attendees.filter(a => a.name !== name);
  }

  get isFull() {
    return this.attendees.length >= this.venue.capacity;
  }

  get duration() {
    return this.endHour - this.startHour;
  }

  get attendeeCount() {
    return this.attendees.length;
  }
}

class Calendar {
  constructor(name) {
    this.name = name;
    this.events = [];
  }

  addEvent(event) {
    this.events.push(event);
  }

  cancelEvent(eventName) {
    const event = this.events.find(e => e.name === eventName);
    if (!event) return;
    event.venue.cancelBooking(eventName);
    event.attendees.forEach(a => a.cancel(eventName));
    this.events = this.events.filter(e => e.name !== eventName);
  }

  getEventsOnDate(date) {
    return this.events.filter(e => e.date === date);
  }

  getEventsByVenue(venueName) {
    return this.events.filter(e => e.venue.name === venueName);
  }
}

