class Pipeline {
  constructor(data) {
    this._data = data;
    this._steps = [];
  }

  static from(data) {
    return new Pipeline(data);
  }

  // Add chainable methods: map, filter, sortBy, take, groupBy
  // Add terminal methods: execute, count, sum
}

exercises.solution:
class Pipeline {
  constructor(data) {
    this._data = data;
    this._steps = [];
  }

  static from(data) {
    return new Pipeline([...data]);
  }

  map(fn) {
    this._steps.push({ type: 'map', fn });
    return this;
  }

  filter(fn) {
    this._steps.push({ type: 'filter', fn });
    return this;
  }

  sortBy(fn) {
    this._steps.push({ type: 'sortBy', fn });
    return this;
  }

  take(n) {
    this._steps.push({ type: 'take', n });
    return this;
  }

  groupBy(fn) {
    this._steps.push({ type: 'groupBy', fn });
    return this;
  }

  execute() {
    let result = [...this._data];
    for (const step of this._steps) {
      switch (step.type) {
        case 'map': result = result.map(step.fn); break;
        case 'filter': result = result.filter(step.fn); break;
        case 'sortBy': result = [...result].sort(step.fn); break;
        case 'take': result = result.slice(0, step.n); break;
        case 'groupBy': {
          const groups = {};
          for (const item of result) {
            const key = step.fn(item);
            if (!groups[key]) groups[key] = [];
            groups[key].push(item);
          }
          return groups;
        }
      }
    }
    return result;
  }

  count() {
    const result = this.execute();
    return Array.isArray(result) ? result.length : Object.keys(result).length;
  }

  sum(key) {
    const result = this.execute();
    return result.reduce((s, item) => s + (item[key] || 0), 0);
  }
}

