exercises.solution:
class Product {
  constructor(name, price, stock) {
    this.name = name;
    this.price = price;
    this.stock = stock;
  }
  reduceStock(qty) {
    if (this.stock < qty) return false;
    this.stock -= qty;
    return true;
  }
  restock(qty) { this.stock += qty; }
  get isAvailable() { return this.stock > 0; }
}

class Discount {
  constructor(code, type, value) {
    this.code = code;
    this.type = type;
    this.value = value;
  }
  apply(subtotal) {
    if (this.type === 'percent') return Math.max(0, subtotal * (1 - this.value / 100));
    return Math.max(0, subtotal - this.value);
  }
}

class Cart {
  constructor(owner) {
    this.owner = owner;
    this.items = [];
    this._discount = null;
  }
  addItem(product, qty) {
    if (product.stock < qty) return false;
    this.items.push({ product, qty });
    return true;
  }
  removeItem(productName) {
    this.items = this.items.filter(i => i.product.name !== productName);
  }
  get subtotal() {
    return this.items.reduce((sum, i) => sum + i.product.price * i.qty, 0);
  }
  applyDiscount(discount) { this._discount = discount; }
  get total() {
    const sub = this.subtotal;
    return this._discount ? this._discount.apply(sub) : sub;
  }
  get itemCount() {
    return this.items.reduce((sum, i) => sum + i.qty, 0);
  }
}

class Order {
  constructor(cart) {
    this.cart = cart;
    this._status = 'pending';
    this._total = cart.total;
    this._items = cart.items.map(i => ({ name: i.product.name, qty: i.qty, price: i.product.price }));
  }
  process() {
    if (this._status !== 'pending') return false;
    for (const item of this.cart.items) {
      if (!item.product.reduceStock(item.qty)) return false;
    }
    this._status = 'processed';
    return this._total;
  }
  cancel() {
    if (this._status !== 'processed') return false;
    for (const item of this.cart.items) {
      item.product.restock(item.qty);
    }
    this._status = 'cancelled';
    return true;
  }
  get status() { return this._status; }
  get receipt() {
    const items = this._items.map(i => i.qty + 'x ' + i.name).join(', ');
    return 'Order for ' + this.cart.owner + ': ' + items + '. Total: $' + this._total.toFixed(2);
  }
}

class Store {
  constructor(name) {
    this.name = name;
    this.products = [];
    this.orders = [];
  }
  addProduct(product) { this.products.push(product); }
  createCart(owner) { return new Cart(owner); }
  checkout(cart) {
    const order = new Order(cart);
    const result = order.process();
    if (result !== false) this.orders.push(order);
    return order;
  }
  get revenue() {
    return this.orders.filter(o => o.status === 'processed').reduce((sum, o) => sum + o._total, 0);
  }
  get orderCount() {
    return this.orders.filter(o => o.status === 'processed').length;
  }
}

