function selectionSort(arr) {
  const result = [...arr];
  for (let i = 0; i < result.length; i++) {
    let minIdx = i;
    for (let j = i + 1; j < result.length; j++) {
      if (result[j] < result[minIdx]) {
        minIdx = j;
      }
    }
    if (minIdx !== i) {
      const temp = result[i];
      result[i] = result[minIdx];
      result[minIdx] = temp;
    }
  }
  return result;
}

