exercises.solution:
function compress(str) {
  if (str.length === 0) return '';

  let compressed = '';
  let count = 1;

  for (let i = 1; i <= str.length; i++) {
    if (i < str.length && str[i] === str[i - 1]) {
      count++;
    } else {
      compressed += str[i - 1] + count;
      count = 1;
    }
  }

  return compressed.length < str.length ? compressed : str;
}

