  // Return { protocol, host, path, query }
}

exercises.solution:
function parseURL(url) {
  const match = url.match(/^(https?|ftp):\/\/([^\/\?]+)(\/[^\?]*)?(?:\?(.*))?$/);
  if (!match) return { protocol: "", host: "", path: "", query: "" };
  return {
    protocol: match[1] || "",
    host: match[2] || "",
    path: match[3] || "",
    query: match[4] || "",
  };
}

