exercises.solution:
function parseLogs(logString) {
  if (!logString) return [];
  const regex = /\[(.+?)\]\s+(\w+):\s+(.+)/;
  return logString.split('\n')
    .map(line => line.match(regex))
    .filter(m => m !== null)
    .map(m => ({ timestamp: m[1], level: m[2], message: m[3] }));
}

