exercises.solution:
function interpolate(template, data) {
  return template.replace(/\{\{([\w.]+)\}\}/g, (_, key) => {
    const parts = key.split('.');
    let val = data;
    for (const p of parts) {
      val = val != null ? val[p] : undefined;
    }
    return val !== undefined ? val : '{{' + key + '}}';
  });
}

