exercises.starterCode:
// Build a tokenizer: tokenize(input, rules) → [{type, value}]
// rules: [{type: string, pattern: RegExp}]
// Skip whitespace between tokens
// Unknown chars → {type: "UNKNOWN", value: char}
function tokenize(input, rules) {

}

exercises.solution:
function tokenize(input, rules) {
  const tokens = [];
  let pos = 0;
  while (pos < input.length) {
    // Skip whitespace
    const wsMatch = input.slice(pos).match(/^\s+/);
    if (wsMatch) {
      pos += wsMatch[0].length;
      if (pos >= input.length) break;
    }
    let matched = false;
    for (const rule of rules) {
      const re = new RegExp('^(?:' + rule.pattern.source + ')');
      const m = input.slice(pos).match(re);
      if (m) {
        tokens.push({ type: rule.type, value: m[0] });
        pos += m[0].length;
        matched = true;
        break;
      }
    }
    if (!matched) {
      tokens.push({ type: 'UNKNOWN', value: input[pos] });
      pos++;
    }
  }
  return tokens;
}

