exercises.starterCode:
function collectText(element) {
  // Recursively walk the DOM tree
  // Collect text from text nodes (node.nodeType === 3)
  // Return array of trimmed, non-empty text
}

exercises.solution:
function collectText(element) {
  const result = [];
  for (const node of element.childNodes) {
    if (node.nodeType === 3) {
      const text = node.textContent.trim();
      if (text) result.push(text);
    } else if (node.nodeType === 1) {
      result.push(...collectText(node));
    }
  }
  return result;
}

