exercises.starterCode:
function debounceInput(inputEl, callback) {
  // Listen for 'input' events on inputEl
  // Only call callback(inputEl.value) after 300ms of no input
}

exercises.solution:
function debounceInput(inputEl, callback) {
  let timer;
  inputEl.addEventListener('input', () => {
    clearTimeout(timer);
    timer = setTimeout(() => {
      callback(inputEl.value);
    }, 300);
  });
}

