exercises.starterCode:
function makeSortable(list) {
  // your code here
  // Make each <li> draggable
  // Handle dragstart, dragover, drop to reorder
}

exercises.solution:
function makeSortable(list) {
  let dragged = null;
  const items = list.querySelectorAll('li');
  items.forEach(item => {
    item.draggable = true;
    item.addEventListener('dragstart', (e) => {
      dragged = item;
    });
    item.addEventListener('dragover', (e) => {
      e.preventDefault();
    });
    item.addEventListener('drop', (e) => {
      e.preventDefault();
      if (dragged && dragged !== item) {
        list.insertBefore(dragged, item);
      }
    });
  });
}

