exercises.starterCode:
// Virtual node structure:
// { tag: string, props: object, children: (vnode | string)[] }
//
// Patch types:
// { type: 'REPLACE', path: number[], newNode: vnode | string }
// { type: 'PROPS',   path: number[], props: object }
// { type: 'REMOVE',  path: number[] }
// { type: 'ADD',     path: number[], newNode: vnode | string }

function diff(oldTree, newTree) {
  // Compare two virtual DOM trees
  // Return array of patch objects
}

exercises.solution:
function diff(oldTree, newTree, path) {
  if (path === undefined) path = [];
  const patches = [];

  // Both are strings (text nodes)
  if (typeof oldTree === 'string' && typeof newTree === 'string') {
    if (oldTree !== newTree) {
      patches.push({ type: 'REPLACE', path: [...path], newNode: newTree });
    }
    return patches;
  }

  // One is string, other is element (or vice versa)
  if (typeof oldTree !== typeof newTree) {
    patches.push({ type: 'REPLACE', path: [...path], newNode: newTree });
    return patches;
  }

  // Different tags
  if (oldTree.tag !== newTree.tag) {
    patches.push({ type: 'REPLACE', path: [...path], newNode: newTree });
    return patches;
  }

  // Same tag — check props
  const propChanges = {};
  let hasChanges = false;
  const allKeys = new Set([...Object.keys(oldTree.props || {}), ...Object.keys(newTree.props || {})]);
  for (const key of allKeys) {
    const oldVal = (oldTree.props || {})[key];
    const newVal = (newTree.props || {})[key];
    if (oldVal !== newVal) {
      propChanges[key] = newVal !== undefined ? newVal : null;
      hasChanges = true;
    }
  }
  if (hasChanges) {
    patches.push({ type: 'PROPS', path: [...path], props: propChanges });
  }

  // Diff children
  const oldChildren = oldTree.children || [];
  const newChildren = newTree.children || [];
  const maxLen = Math.max(oldChildren.length, newChildren.length);
  for (let i = 0; i < maxLen; i++) {
    const childPath = [...path, i];
    if (i >= oldChildren.length) {
      patches.push({ type: 'ADD', path: childPath, newNode: newChildren[i] });
    } else if (i >= newChildren.length) {
      patches.push({ type: 'REMOVE', path: childPath });
    } else {
      patches.push(...diff(oldChildren[i], newChildren[i], childPath));
    }
  }

  return patches;
}

