  })();

  const test2 = (async () => {
    const r = await chainPromises([], 42);
    return r === 42;
  })();

  const test3 = (async () => {
    try {
      await chainPromises([async x => x * 2, async x => { throw new Error('stop'); }, async x => x + 1], 5);
      return false;
    } catch (e) {
      return e.message === 'stop';
    }
  })();

  const test4 = (async () => {
    const r = await chainPromises([async x => x.toUpperCase(), async x => x + '!'], 'hello');
    return r === 'HELLO!';
  })();

  return Promise.all([test1, test2, test3, test4]).then(([r1, r2, r3, r4]) => [
    { pass: r1, description: 'Chains two async functions: 5 → 10 → 11', got: String(r1) },
    { pass: r2, description: 'Empty array returns initialValue', got: String(r2) },
    { pass: r3, description: 'Rejection stops the chain', got: String(r3) },
    { pass: r4, description: 'Works with string transformations', got: String(r4) },
  ]);
}

