  const test1 = (async () => {
    const r = await asyncMap([1, 2, 3], async n => n * 2);
    return JSON.stringify(r) === '[2,4,6]';
  })();

  const test2 = (async () => {
    const r = await asyncMap([], async n => n);
    return JSON.stringify(r) === '[]';
  })();

  const test3 = (async () => {
    const order = [];
    await asyncMap([30, 10, 20], async (ms, i) => {
      await new Promise(r => setTimeout(r, ms));
      order.push(i);
      return ms;
