    return order[0] === 1 && order[1] === 2 && order[2] === 0;
  })();

  const test4 = (async () => {
    try {
      await asyncMap([1, 2, 3], async n => { if (n === 2) throw new Error('bad'); return n; });
      return false;
    } catch (e) {
      return e.message === 'bad';
    }
  })();

  return Promise.all([test1, test2, test3, test4]).then(([r1, r2, r3, r4]) => [
