exercises.starterCode:
function createErrorBoundary() {
  // your code here
}

exercises.solution:
function createErrorBoundary() {
  const errors = [];
  const results = [];

  return {
    async run(asyncFn) {
      try {
        const result = await asyncFn();
        results.push(result);
        return result;
      } catch (err) {
        errors.push(err);
        return undefined;
      }
    },
    get errors() { return errors; },
    get hasErrors() { return errors.length > 0; },
    get results() { return results; },
  };
}

