    const b = createErrorBoundary();
    const r = await b.run(async () => { throw new Error('oops'); });
    return r === undefined && b.errors.length === 1 && b.errors[0].message === 'oops';
  })();

  const test3 = (async () => {
    const b = createErrorBoundary();
    await b.run(async () => 1);
    await b.run(async () => { throw new Error('x'); });
    await b.run(async () => 2);
