exercises.solution:
function createMiddleware() {
  const stack = [];

  return {
    use(fn) {
      stack.push(fn);
    },
    async run(context) {
      let index = 0;
      async function next() {
        if (index < stack.length) {
          const fn = stack[index++];
          await fn(context, next);
        }
      }
      await next();
      return context;
    }
  };
}

