exercises.solution:
function avgTempByStation(readings) {
  const groups = {};
  for (const r of readings) {
    if (!groups[r.station]) groups[r.station] = [];
    groups[r.station].push(r.tempC);
  }
  const result = {};
  for (const [station, temps] of Object.entries(groups)) {
    result[station] = Math.round((temps.reduce((a, b) => a + b, 0) / temps.length) * 10) / 10;
  }
  return result;
}

function hottestDay(readings) {
  let maxTemp = -Infinity, maxDate = '';
  for (const r of readings) {
    if (r.tempC > maxTemp) { maxTemp = r.tempC; maxDate = r.date; }
  }
  return maxDate;
}

function windyDays(readings, threshold) {
  const dates = new Set();
  for (const r of readings) {
    if (r.wind > threshold) dates.add(r.date);
  }
  return [...dates];
}

function dailySummary(readings) {
  const groups = {};
  for (const r of readings) {
    if (!groups[r.date]) groups[r.date] = { temps: [], maxWind: 0 };
    groups[r.date].temps.push(r.tempC);
    groups[r.date].maxWind = Math.max(groups[r.date].maxWind, r.wind);
  }
  return Object.entries(groups)
    .map(([date, d]) => ({
      date,
      avgTemp: Math.round((d.temps.reduce((a, b) => a + b, 0) / d.temps.length) * 10) / 10,
      maxWind: d.maxWind,
    }))
    .sort((a, b) => a.date.localeCompare(b.date));
}

