exercises.solution:
class Graph {
  constructor() {
    this.adjacencyList = {};
  }

  addVertex(vertex) {
    if (!this.adjacencyList[vertex]) this.adjacencyList[vertex] = [];
  }

  addEdge(v1, v2) {
    if (this.adjacencyList[v1] && this.adjacencyList[v2]) {
      if (!this.adjacencyList[v1].includes(v2)) this.adjacencyList[v1].push(v2);
      if (!this.adjacencyList[v2].includes(v1)) this.adjacencyList[v2].push(v1);
    }
  }

  removeEdge(v1, v2) {
    if (this.adjacencyList[v1]) this.adjacencyList[v1] = this.adjacencyList[v1].filter(v => v !== v2);
    if (this.adjacencyList[v2]) this.adjacencyList[v2] = this.adjacencyList[v2].filter(v => v !== v1);
  }

  removeVertex(vertex) {
    if (!this.adjacencyList[vertex]) return;
    for (const neighbor of this.adjacencyList[vertex]) {
      this.adjacencyList[neighbor] = this.adjacencyList[neighbor].filter(v => v !== vertex);
    }
    delete this.adjacencyList[vertex];
  }

  getNeighbors(vertex) {
    return this.adjacencyList[vertex] || [];
  }

  hasEdge(v1, v2) {
    return !!(this.adjacencyList[v1] && this.adjacencyList[v1].includes(v2));
  }

  bfs(start) {
    if (!this.adjacencyList[start]) return [];
    const visited = new Set();
    const queue = [start];
    const result = [];
    visited.add(start);
    while (queue.length > 0) {
      const vertex = queue.shift();
      result.push(vertex);
      for (const neighbor of this.adjacencyList[vertex]) {
        if (!visited.has(neighbor)) {
          visited.add(neighbor);
          queue.push(neighbor);
        }
      }
    }
    return result;
  }

  get size() {
    return Object.keys(this.adjacencyList).length;
  }
}

